import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React, { ReactNode } from "react"
import ScheduleSelector from 'react-schedule-selector'

interface State {
  schedule: Array<Date>
  selectedEvent: string
}

class ScheduleComponent extends StreamlitComponentBase<State> {
  public state = { schedule: [], selectedEvent: '' }

  public inputDatas = () => {
    const somedays = [
      new Date("2023-12-18T01:00:00Z"),
      new Date("2023-12-18T02:00:00Z"),
      new Date("2023-12-18T03:00:00Z"),
      new Date("2023-12-18T04:00:00Z"),
      new Date("2023-12-18T05:00:00Z"),
    ]
    // somedays가 비어 있지 않으면 schedule state에 추가
    if (somedays.length > 0) {
      this.setState({ schedule: [...this.state.schedule, ...somedays] });
    }
  }

  public render = (): ReactNode => {
    // 기존 props
    const numDays = this.props.args["num_days"]
    const minTime = this.props.args["min_time"]
    const maxTime = this.props.args["max_time"]
    const hourlyChunks = this.props.args["hourly_chunks"]
    const dateFormat = this.props.args["date_format"]
    const startDate = this.props.args["start_date"]

    // 새로 추가된 events prop
    const events = this.props.args["events"]



    return (
      <div>
        {/* 이벤트 버튼 */}
        {events.map((event:any) => (
          <button
            key={event}
            style={{ backgroundColor: this.state.selectedEvent === event ? 'blue' : 'grey' }}
            onClick={() => this.setState({ selectedEvent: event })}
          >
            {event}
          </button>
        ))}

        <ScheduleSelector
          selection={this.state.schedule}
          numDays={numDays}
          minTime={minTime}
          maxTime={maxTime}
          hourlyChunks={hourlyChunks}
          dateFormat={dateFormat}
          startDate={new Date(startDate)}
          columnGap="2px"
          rowGap="2px"
          onChange={this.handleChange}
          // 선택 영역 스타일 변경 (예시)
          // selectedColor={this.getSelectedColor(events)}
          // renderDateCell={(date, selected, refSetter) => (
          //   <div
          //     style={{
          //       backgroundColor: selected
          //         ? somedays.find((d) => d.getTime() === date.getTime())
          //           ? "red"
          //           : "blue"
          //         : "lightgray",
          //       width: "100%",
          //       height: "100%"
          //     }}
          //   />
          // )}
        />
      </div>
    )
  }

  private getSelectedColor = (events:any): string => {
    // 선택된 이벤트에 따라 다른 색상 반환 (색상 코드는 예시입니다)
    switch (this.state.selectedEvent) {
      case events[0]: return '#FF6C6C';
      case events[1]: return '#FFBD45';
      case events[2]: return '#3D9DF3';
      case events[3]: return '#3df355';
      case events[4]: return '#b33df3';
      // 추가 이벤트 색상 조건
      default: return '#3d43f3'; // 기본 색상 (연한 파랑)
    }
  }
  private handleChange = (newSchedule: any) => {
    this.setState({ schedule: newSchedule })

    const formattedDates: string[] = newSchedule.map((date:any) => {
      const options: Intl.DateTimeFormatOptions = {
        weekday: 'short',
        hour: 'numeric',
        minute: 'numeric',
        hour12: false,
      };
      const formattedDate: string = new Intl.DateTimeFormat('en-US', options).format(date);
      const [weekday, time]: string[] = formattedDate.split(' ');
      return { [weekday]: time };
    });

    const jsonDates: { [key: string]: string[] } = formattedDates.reduce(
      (acc:any, obj:any) => {
        const key: string = Object.keys(obj)[0];
        const value: string = Object.values(obj)[0] as string;
        if (acc[key]) {
          acc[key].push(value);
        } else {
          acc[key] = [value];
        }
        return acc;
      },
      {}
    );
    Streamlit.setComponentValue(jsonDates)
  }
}

export default withStreamlitConnection(ScheduleComponent)
